import React, { useRef, useState, useEffect } from "react";
import "./Navbar.css";
import logo from "../Asset/logo.png";
import cart_icon from "../Asset/cart_icon.png";
import { Link } from "react-router-dom";
import all_product from "../Asset/all_product";
import { useContext } from "react";
import { ShopContext } from "../../Context/ShopContext";
import nav_dropdown from "../Asset/dropdown_icon.png";
import user_icon from "../Asset/user.png";

const Navbar = () => {
  const [menu, setMenu] = useState("shop");
  const { getTotalItem } = useContext(ShopContext);
  const [isLogin, setLogin] = useState(false);
  const [userData, setUserData] = useState(null);
  const menuRef = useRef();

  const dropDown_toggle = (e) => {
    menuRef.current.classList.toggle("nav-menu-visible");
    e.target.classList.toggle("open");
  };

  const handleLoginCheck = () => {
    setMenu("");
  };

  const getGreeting = () => {
    const date = new Date();
    const currentHour = date.getHours();

    let greetingMessage = "";

    if (currentHour >= 5 && currentHour < 12) {
      greetingMessage = "Chào buổi sáng";
    } else if (currentHour >= 12 && currentHour < 17) {
      greetingMessage = "Chào buổi trưa";
    } else if (currentHour >= 17 && currentHour < 19) {
      greetingMessage = "Chào buổi chiều";
    } else {
      greetingMessage = "Chào buổi tối";
    }

    if (userData) {
      greetingMessage += `, <strong style="color: #41a0ff;">Kiều Tấn Quốc</strong>`;
    }

    return <div dangerouslySetInnerHTML={{ __html: greetingMessage + "!" }} />;
  };

  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem("auth-token");
      if (token) {
        setLogin(true);
      } else {
        setLogin(false);
      }
      const email = localStorage.getItem("email");
      const resp = await fetch(`http://localhost:4000/profile/${email}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!resp) {
        setLogin(false);
        throw new Error(`Lỗi: ${response.status} - ${response.statusText}`);
      }
      const userData = await resp.json();
      setUserData(userData.user);
    } catch (error) {
      setLogin(false);
    }
  };
  useEffect(() => {
    fetchUserData();
  }, []);

  return (
    <div>
      <div className="contact-nav">{getGreeting()}</div>
      <div className="navbar">
        <div className="nav">
          <Link to={"/"} style={{ textDecoration: "none", color: "black" }}>
            <div
              className="nav-logo"
              onClick={() => {
                setMenu("shop");
              }}
            >
              <img src={logo} alt="" />
              <p>iClock</p>
            </div>
          </Link>
          <img
            className="nav-dropdown"
            onClick={dropDown_toggle}
            src={nav_dropdown}
            alt=""
          />
        </div>
        {/* <i
          className="nav-dropdown fa-solid fa-caret-down"
          onClick={dropDown_toggle}
        ></i> */}
        <ul ref={menuRef} className="nav-menu">
          <li
            onClick={() => {
              setMenu("shop");
            }}
          >
            <Link style={{ textDecoration: "none", color: "black" }} to="/">
              Trang chủ
            </Link>
            {menu === "shop" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("mens");
            }}
          >
            <Link style={{ textDecoration: "none", color: "black" }} to="/mens">
              Phái Mạnh
            </Link>
            {menu === "mens" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("womens");
            }}
          >
            <Link
              style={{ textDecoration: "none", color: "black" }}
              to="/womens"
            >
              Phái Đẹp
            </Link>
            {menu === "womens" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("kids");
            }}
          >
            <Link style={{ textDecoration: "none", color: "black" }} to="/kids">
              Trẻ em
            </Link>
            {menu === "kids" ? <hr /> : <></>}
          </li>
        </ul>
        <div className="nav-login-cart">
          {localStorage.getItem("auth-token") ? (
            <div
            // onClick={() => {
            //   localStorage.removeItem("auth-token");
            //   window.location.replace("/");
            // }}
            >
              {/* Đăng xuất */}
            </div>
          ) : (
            <Link to="/login">
              <button onClick={handleLoginCheck}>Đăng nhập</button>
            </Link>
          )}
          <Link to="/cart">
            <img src={cart_icon} alt="" />
          </Link>
          <div className={"nav-cart-count"}>{getTotalItem()}</div>
          <Link to="/profile">
            <img
              style={{ width: "42px", height: "42px" }}
              src={user_icon}
              alt=""
            />
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
